/*
疯狂星期五
活动时间：长期活动

活动入口：京东app-京东秒杀-签到领红包
脚本兼容: QuantumultX, Surge, Loon, JSBox, Node.js

 */
const $ = new Env('疯狂星期五');

//IOS等用户直接用NobyDa的jd cookie
let cookiesArr = [], cookie = '', message;
// 助力邀请码，自定义请配置环境变量MP_H5_REDPACKET_ID，否则助力作者
let redpacketId=0

const JD_API_HOST = 'https://api.m.jd.com/client.action';
!(async () => {
  await requireConfig();
  if(!redpacketId) {
    shareCodesFormat()
  }
  if (!cookiesArr[0]) {
    $.msg($.name, '【提示】请先获取京东账号一cookie\n直接使用NobyDa的京东签到获取', 'https://bean.m.jd.com/bean/signIndex.action', {"open-url": "https://bean.m.jd.com/bean/signIndex.action"});
    return;
  }
  for (let i = 0; i < cookiesArr.length; i++) {
    if (cookiesArr[i]) {
      cookie = cookiesArr[i];
      $.UserName = decodeURIComponent(cookie.match(/pt_pin=([^; ]+)(?=;?)/) && cookie.match(/pt_pin=([^; ]+)(?=;?)/)[1])
      $.index = i + 1;
      $.isLogin = true;
      $.nickName = '';
      message = '';
      //await TotalBean();
      console.log(`\n******开始【京东账号${$.index}】${$.nickName || $.UserName}*********\n`);
      if (!$.isLogin) {
        $.msg($.name, `【提示】cookie已失效`, `京东账号${$.index} ${$.nickName || $.UserName}\n请重新登录获取\nhttps://bean.m.jd.com/`, {"open-url": "https://bean.m.jd.com/"});

        if ($.isNode()) {
          await notify.sendNotify(`${$.name}cookie已失效 - ${$.UserName}`, `京东账号${$.index} ${$.UserName}\n请重新登录获取cookie`);
        }
        continue
      }
      await h5activityIndex();
      if(!$.redpacketId){
        await h5launch();
      }
      if(redpacketId != $.redpacketId){
        await h5assist(redpacketId)
      } else {
        console.log('跳过自己的助力码')
      }
      
    }
  }
})()
  .catch((e) => {
    $.log('', `❌ ${$.name}, 失败! 原因: ${e}!`, '')
  })
  .finally(() => {
    $.done();
  })

  function h5activityIndex(){
    return new Promise(resolve => {
      $.post(taskPostUrl('h5activityIndex', {"clientInfo":{},"isjdapp":1}), (err, resp, data) => {
        try {
          if (err) {
            console.log(`${err},${jsonParse(resp.body)['message']}`)
            console.log(`${$.name} API请求失败，请检查网路重试`)
          } else {
            if (safeGet(data)) {
              data = JSON.parse(data)
              // console.log(data.data.result.redpacketInfo)
              if (data.code === 0) {
                // {"code":0,"data":{"biz_code":0,"code":0,"result":{"followCode":0,"redPacketId":319074292,"status":0,"statusDesc":"成功"},"success":true},"rtn_code":0}
                if(data.data && data.data.result && data.data.result.redpacketInfo && data.data.result.redpacketInfo.id){
                  console.log(`红包助力码：${data.data.result.redpacketInfo.id}`)
                  $.redpacketId = data.data.result.redpacketInfo.id
                  addShareCode($.UserName, data.data.result.redpacketInfo.id, 'mp_h5')
                }
               
              }
            }
          }
        } catch (e) {
          $.logErr(e, resp)
        } finally {
          resolve(data);
        }
      })
    })
  }

  function h5launch(){
    return new Promise(resolve => {
      $.post(taskPostUrl('h5launch', {"clientInfo":{},"followShop":0,"promUserState":""}), (err, resp, data) => {
        try {
          if (err) {
            console.log(`${err},${jsonParse(resp.body)['message']}`)
            console.log(`${$.name} API请求失败，请检查网路重试`)
          } else {
            if (safeGet(data)) {
              console.log(data)
              data = JSON.parse(data)
              if (data.code === 0) {
                // {"code":0,"data":{"biz_code":0,"code":0,"result":{"followCode":0,"redPacketId":319074292,"status":0,"statusDesc":"成功"},"success":true},"rtn_code":0}
                if(data.data && data.data.result && data.data.result.redPacketId){
                  console.log(`红包助力码：${data.data.result.redPacketId}`)
                  addShareCode($.UserName, data.data.result.redPacketId, 'mp_h5')
                }
               
              }
            }
          }
        } catch (e) {
          $.logErr(e, resp)
        } finally {
          resolve(data);
        }
      })
    })
  }

function h5assist(inviteId) {
  return new Promise(resolve => {
    $.post(taskPostUrl('jinli_h5assist', {"clientInfo":{},"redPacketId":inviteId + "","followShop":0,"promUserState":""}), (err, resp, data) => {
      try {
        if (err) {
          console.log(`${err},${jsonParse(resp.body)['message']}`)
          console.log(`${$.name} API请求失败，请检查网路重试`)
        } else {
          if (safeGet(data)) {
            console.log(data)
            data = JSON.parse(data)
            if (data.code === 200) {
              $.encryptProjectId = data.result.assignmentResult.encryptProjectId
              console.log(`活动名称：${data.result.assignmentResult.projectName}`)
            }
          }
        }
      } catch (e) {
        $.logErr(e, resp)
      } finally {
        resolve(data);
      }
    })
  })
}


function taskPostUrl(function_id = 'jinli_h5assist', body = {}) {
  let url = `${JD_API_HOST}/api?appid=jd_mp_h5&functionId=${function_id}&loginType=2&client=jd_mp_h5&clientVersion=10.0.5&osVersion=AndroidOS&d_brand=Xiaomi&d_model=Xiaomi`;

  return {
    url,
    body: `body=${escape(JSON.stringify(body))}`,
    headers: {
      "Cookie": cookie,
      "origin": "https://h5.m.jd.com",
      "referer": "https://h5.m.jd.com/babelDiy/Zeus/2NUvze9e1uWf4amBhe1AV6ynmSuH/index.html",
      'Content-Type': 'application/x-www-form-urlencoded',
      "X-Requested-With": "com.jingdong.app.mall",
      "User-Agent": $.isNode() ? (process.env.JD_USER_AGENT ? process.env.JD_USER_AGENT : (require('./USER_AGENTS').USER_AGENT)) : ($.getdata('JDUA') ? $.getdata('JDUA') : "jdapp;iPhone;9.4.4;14.3;network/4g;Mozilla/5.0 (iPhone; CPU iPhone OS 14_3 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148;supportJDSHWK/1"),
    }
  }
}

function TotalBean() {
  return new Promise(async resolve => {
    const options = {
      "url": `https://wq.jd.com/user/info/QueryJDUserInfo?sceneval=2`,
      "headers": {
        "Accept": "application/json,text/plain, */*",
        "Content-Type": "application/x-www-form-urlencoded",
        "Accept-Encoding": "gzip, deflate, br",
        "Accept-Language": "zh-cn",
        "Connection": "keep-alive",
        "Cookie": cookie,
        "Referer": "https://wqs.jd.com/my/jingdou/my.shtml?sceneval=2",
        "User-Agent": $.isNode() ? (process.env.JD_USER_AGENT ? process.env.JD_USER_AGENT : (require('./USER_AGENTS').USER_AGENT)) : ($.getdata('JDUA') ? $.getdata('JDUA') : "jdapp;iPhone;9.4.4;14.3;network/4g;Mozilla/5.0 (iPhone; CPU iPhone OS 14_3 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148;supportJDSHWK/1")
      }
    }
    $.post(options, (err, resp, data) => {
      try {
        if (err) {
          console.log(`${JSON.stringify(err)}`)
          console.log(`${$.name} API请求失败，请检查网路重试`)
        } else {
          if (data) {
            data = JSON.parse(data);
            if (data['retcode'] === 13) {
              $.isLogin = false; //cookie过期
              return
            }
            if (data['retcode'] === 0) {
              $.nickName = (data['base'] && data['base'].nickname) || $.UserName;
            } else {
              $.nickName = $.UserName
            }
          } else {
            console.log(`京东服务器返回空数据`)
          }
        }
      } catch (e) {
        $.logErr(e, resp)
      } finally {
        resolve();
      }
    })
  })
}

function safeGet(data) {
  try {
    if (typeof JSON.parse(data) == "object") {
      return true;
    }
  } catch (e) {
    console.log(e);
    console.log(`京东服务器访问数据为空，请检查自身设备网络情况`);
    return false;
  }
}

function jsonParse(str) {
  if (typeof str == "string") {
    try {
      return JSON.parse(str);
    } catch (e) {
      console.log(e);
      $.msg($.name, '', '不要在BoxJS手动复制粘贴修改cookie')
      return [];
    }
  }
}
function addShareCode($pt_pin, $code, $type, cookie) {
  //console.log(`addShareCode`,$pt_pin, $code)
  return new Promise(async (resolve) => {
    $.get(
      {
        url: `https://admin.0xaa.cn/api/share_code/add?type=${$type}&code=${$code}&pt_pin=${$pt_pin}`,
        timeout: 20000,
        headers:{
          Cookie: cookie
        }
      },
      (err, resp, data) => {
        //console.log('addShareCode result:', err, data);
        try {
          if (err) {
            console.log(`${JSON.stringify(err)}`);
            console.log(`${$.name} API请求失败，请检查网路重试`);
          } else {
            if (data) {
              data = JSON.parse(data);
            }
          }
        } catch (e) {
          $.logErr(e, resp);
        } finally {
          resolve(data);
        }
      }
    );
    await $.wait(20000);
    resolve();
  });
}

function readShareCode($type, gt=0) {
  return new Promise(async (resolve) => {
    $.get(
      {
        url: `https://admin.0xaa.cn/api/share_code/query/type/${$type}/limit/1?gt=${gt}`,
        timeout: 10000,
      },
      (err, resp, data) => {
        try {
          if (err) {
            console.log(`${JSON.stringify(err)}`);
            console.log(`${$.name} API请求失败，请检查网路重试`);
          } else {
            if (data) {
              data = JSON.parse(data);
              // console.log('readShareCode', data)
            }
          }
        } catch (e) {
          $.logErr(e, resp);
        } finally {
          resolve(data);
        }
      }
    );
  });
}
function shareCodesFormat() {
  return new Promise(async resolve => {
    const readShareCodeRes = await readShareCode('mp_h5');
    if (readShareCodeRes && readShareCodeRes.code === 1 && readShareCodeRes.data && readShareCodeRes.data.length > 0) {
      redpacketId = readShareCodeRes.data[0]
    }
    console.log(`第${$.index}个京东账号将要助力的好友${redpacketId}`)
    resolve();
  })
}
function requireConfig() {
  return new Promise(resolve => {
    console.log('开始获取疯狂星期五配置文件\n')
    notify = $.isNode() ? require('./sendNotify') : '';
    //Node.js用户请在jdCookie.js处填写京东ck;
    const jdCookieNode = $.isNode() ? require('./jdCookie.js') : '';
    
    //IOS等用户直接用NobyDa的jd cookie
    if ($.isNode()) {
      Object.keys(jdCookieNode).forEach((item) => {
        if (jdCookieNode[item]) {
          cookiesArr.push(jdCookieNode[item])
        }
      })
      if (process.env.JD_DEBUG && process.env.JD_DEBUG === 'false') console.log = () => {};
    } else {
      cookiesArr = [$.getdata('CookieJD'), $.getdata('CookieJD2'), ...jsonParse($.getdata('CookiesJD') || "[]").map(item => item.cookie)].filter(item => !!item);
    }
    console.log(`共${cookiesArr.length}个京东账号\n`)
    if ($.isNode()) {
      if (process.env.MP_H5_REDPACKET_ID) {
        redpacketId=process.env.MP_H5_REDPACKET_ID
      };
    } else {
      if ($.getdata('MP_H5_REDPACKET_ID')) redpacketId = $.getdata('MP_H5_REDPACKET_ID');
      console.log(`\nBoxJs设置的${$.name}好友邀请码:${$.getdata('MP_H5_REDPACKET_ID') ? $.getdata('MP_H5_REDPACKET_ID') : '暂无'}\n`);
    }

    resolve()
  })
}
